interface Tracks {
	href: string;
	limit: number;
	next: string;
	offset: number;
	previous: string;
	total: number;
	items: SpotifyTrack[];
}

interface SpotifyTrack {
	album: Album;
	artists: ItemArtist[];
	available_markets: string[];
	disc_number: number;
	duration_ms: number;
	explicit: boolean;
	external_ids: ExternalIDS;
	external_urls: ExternalUrls;
	href: string;
	id: string;
	is_playable: boolean;
	linked_from: LinkedFrom;
	restrictions: Restrictions;
	name: string;
	popularity: number;
	preview_url: string;
	track_number: number;
	type: string;
	uri: string;
	is_local: boolean;
}

interface Album {
	album_type: string;
	total_tracks: number;
	available_markets: string[];
	external_urls: ExternalUrls;
	href: string;
	id: string;
	images: Image[];
	name: string;
	release_date: string;
	release_date_precision: string;
	restrictions: Restrictions;
	type: string;
	uri: string;
	artists: AlbumArtist[];
}

interface AlbumArtist {
	external_urls: ExternalUrls;
	href: string;
	id: string;
	name: string;
	type: string;
	uri: string;
}

interface ExternalUrls {
	spotify: string;
}

interface Image {
	url: string;
	height: number;
	width: number;
}

interface Restrictions {
	reason: string;
}

interface ItemArtist {
	external_urls: ExternalUrls;
	followers: Followers;
	genres: string[];
	href: string;
	id: string;
	images: Image[];
	name: string;
	popularity: number;
	type: string;
	uri: string;
}

interface Followers {
	href: string;
	total: number;
}

interface ExternalIDS {
	isrc: string;
	ean: string;
	upc: string;
}

interface LinkedFrom {}

interface SpotifyPlaylist {
	collaborative: boolean;
	description: string;
	external_urls: ExternalUrls;
	followers: Followers;
	href: string;
	id: string;
	images: Image[];
	name: string;
	owner: Owner;
	public: boolean;
	snapshot_id: string;
	tracks: Tracks;
	type: string;
	uri: string;
}

interface Owner {
	external_urls: ExternalUrls;
	followers?: Followers;
	href: string;
	id: string;
	type: string;
	uri: string;
	display_name?: string;
	name?: string;
}

declare const searchTrack: (query: string) => Promise<SpotifyTrack[]>;
declare const searchPlaylist: (query: string) => Promise<SpotifyPlaylist[]>;
declare const getTrackInfo: (id: string) => Promise<SpotifyTrack>;
declare const getPlaylist: (id: string) => Promise<SpotifyPlaylist>;

declare const validateID: (id: string) => boolean;

export { getPlaylist, getTrackInfo, searchPlaylist, searchTrack, validateID };
